import { ButtonGroup, Button, Alert, Row, Col } from 'reactstrap';
import'./Radio.css';

import React, { PureComponent } from 'react'

class Frequency extends PureComponent {

    compare() {

        if (!this.props.prevFreq) return <Alert>No result previous year</Alert>
        if (this.props.value > this.props.prevFreq) return <Alert><img src="./RadioIcons/up.svg"  alt="UP"/></Alert>
        else if (this.props.value < this.props.prevFreq) return <Alert><img src="./RadioIcons/down.svg"  alt="DOWN"/></Alert>
        else if (this.props.value === this.props.prevFreq) return <Alert><img src="./RadioIcons/stable.svg" alt="Stable"/></Alert>
        else if (this.props.value === null) return <Alert>_</Alert>
    }

  render() {       

return <Row>
            <Col sm="12" md={{ size: 8 }} >
                <ButtonGroup className='squareRadio' name="frequency" value={this.props.value} onChange={this.props.parentCallback}>
                    <Button name="frequency" value={0} onClick={this.props.parentCallback} active={this.props.value === "0"}>{0}</Button>
                    <p>dma,</p>
                    <Button name="frequency" value={1} onClick={this.props.parentCallback} active={this.props.value === "1"}>{1}</Button>
                    <Button name="frequency" value={2} onClick={this.props.parentCallback} active={this.props.value === "2"}>{2}</Button>
                    <Button name="frequency" value={3} onClick={this.props.parentCallback} active={this.props.value === "3"}>{3}</Button>
                    <Button name="frequency" value={4} onClick={this.props.parentCallback} active={this.props.value === "4"}>{4}</Button>
                    <Button name="frequency" value={-1} onClick={this.props.parentCallback} active={this.props.value === "-1"}>Not Relevant</Button>
                </ButtonGroup>   
            </Col>
            <Col>
                {this.props.prevFreq && this.compare()}
            </Col>
        </Row>
    }   
}
