export default class MainForm extends React.Component {

    state = {

        // --------- TESTING : UNCOMMENT WHEN SERVER DOWN ----------------
        categories: [],
        motivations: [],
        regularity: [],
        question: [],
        questions: [],
        id: 1,
        name: [],
        incident: [],
        results: [],
        // ----------------------------------------------------------------

        answers: [],
        value: [],
        selectedCat: 1,
        selectedMode: 0,

    };
    handleFormSubmit = () => {
        const { answers } = this.state;
        window.localStorage.setItem('state', JSON.stringify(answers));
    };

    // handleFormSubmit = () => {
    //     const { answers } = this.state;
    //     localStorage.setItem('answer', JSON.stringify(answers));
    //   };
