        //         localStorage.setItem("completedModes", JSON.stringify(dummieArray));

        //     } else {
        //         localStorage.setItem("completedModes", JSON.stringify(completeMode));
        //     }
        // }
    }

    selectCat = (e) => {
        this.setState({ selectedCat: e.target.value })
        localStorage.setItem(this.props.id, JSON.stringify(this.state.answers));
    }
    // onClick = (e) => {
    //             localStorage.setItem("answer", JSON.stringify(this.state.regularity))
    //         }

    // componentDidUpdate(){
    //     onClick = (e) => {
    //         localStorage.setItem("answer", JSON.stringify(this.state.regularity))
    //     }

    // }

    componentDidMount() {

        this.fetchQuestions(1);

        const transportId = this.props.id;
        this.setState({ modeID: transportId })

        // ------------------ Retreiving PREVIOUS PERIOD RESULTS ----------------------

        // if (JSON.parse(localStorage.getItem("periods"))) {

        //     axios.post(`${URLStorage.API_URL}/resultPeriode`, {
        //         ptoID: parseInt(localStorage.getItem("ptoid")),
        //         periodeID: JSON.parse(localStorage.getItem("periods")).previousPeriod,
        //     })
        //         .then(res => {
        //             const previousResult = res.data.filter(item => item.mode_id === transportId).sort((a, b) => a.questionID - b.questionID).map(item => {

        //                 return { prevFreq: item.frequency, };
        //             })
        //             const newStateAnswers = this.state.answers.map((answer, idx) => {
        //                 return { ...answer, ...previousResult[idx] }
        //             })
        //             // // ------------------ ADDING PREVIOUS PERIOD RESULTS TO THE STATE.ANSWERS ----------------------
        //             this.setState({ answers: newStateAnswers });
        //             localStorage.setItem("prevResult", JSON.stringify(previousResult))
        //         })
        // };
        // // -----------------------------------------------------------------


        // axios.get(`${URLStorage.API_URL}/getallincident`)
        //     .then(res => {
        //         const data = res.data
        //         this.setState({
        //             categories: data,
        //             isLoaded: true,
        //         })
        //     })

        // axios.get(`${URLStorage.API_URL}/getallquestions`)
        // .then(res => {
        //     const data = res.data
        //     this.setState({
        //         questions: data,
        //         isLoaded: true,
        //     })
        // })

        // axios.get(`${URLStorage.API_URL}/getallquestions`)
        // .then(response =>(
        //         response.data.map(questions => {
        //           questionTest.push(questions.question);
        //           incidentTest.push(questions.incident);
        //         }),
        //         resultsTest = response.data,
        //         this.setState({question : questionTest, incident : incidentTest, results: resultsTest})
        //       ))
    };



    render() {
        // NEXT / SUBMIT BUTTON SWITCH
        const nextSubmit = this.state.selectedCat === 5 ?
            // || (parseInt((localStorage.getItem("group")) === 1)
            (JSON.parse(localStorage.getItem("completedModes")).filter(item => item === false).length === 0) ?
                <Link to={{ pathname: "/confirmation", state: { answers: this.state.answers } }}>
                    <Button onClick={this.submit} className="submit" >Submit</Button>
                </Link>
                : <>
                    <Button className="submit" disabled>Submit</Button>
                </>
            :
            <Button onClick={this.next} className="submit">Next</Button>;

        //console.log(this.state.categories);
        // console.log(this.state.regularity);
        //console.log(this.state.motivations);
        // console.log(this.state.question);
        // console.log(this.state.answers);
        // console.log(this.state.id);
        return (
            <React.Fragment>
                <Container>
                    <div style={{ display: "flex", justifyContent: "flex-end" }}>
                        {this.state.selectedCat !== 1 && <Button style={{ marginRight: "10px" }} onClick={this.prev} className="submit" >Previous</Button>}
                        {nextSubmit}
                    </div>
                    <Categories idPage={this.state.id} />
                    <br />
                    <br />
                    {this.state.question.map(question => {
                        return (
                            <ul>
                                <li key={this.state.id} style={{ fontSize: "20px" }} >
                                    {question.question}
                                    <DropdownMotivation />
                                    <Frequency
                                    // onClick {
                                    //    ...this.handleFormSubmit
                                    // }
                                    />
                                    <Comments key={question.id + 'coucou'} questionId={question.id} comment={question.comment} parentCallback={this.anyChange(question.id)} />
                                </li>
                            </ul>
                        );
                    })}
                </Container>
            </React.Fragment>
        )
    }
