const Mode = props => {

    const checked = { opacity: "1", padding: "5px", transitionDuration: "0.3s"};   
    const unchecked = { opacity: ".5", padding: "5px", transitionDuration: "0.3s" };
    const path = process.env.PUBLIC_URL + '/modes/' + props.name + '.svg'  
    // console.log(path)
