        <img
            src={path}
            alt={props.name} 
            style={props.grouped ? {maxHeight: props.maxHeight}: {maxHeight: props.maxHeight, cursor: "pointer"}} 

        />
        <h4 style={{ textAlign: "center" }}>
            {props.name}
        </h4>
    </div>

}
export default Mode;
