import React, { Component } from 'react'
import { Button, Header, Icon, Segment } from 'semantic-ui-react'
import Confirmation from '../Confirmation/Confirmation'
import { renderToString } from "react-dom/server";
import html2canvas from 'html2canvas';
import jsPDF from "jspdf";
import './Pdf.css'
import { Link } from "react-router-dom";
//import Reports from '../Reports/Reports';


export default class Pdf extends Component {
  print = () => {
    const string = renderToString(<Confirmation />);
    const pdf = new jsPDF("p", "mm", "a4");

    pdf.fromHTML(string);
    pdf.save("pdf");
  };

  printDocument() {
    const string = renderToString(<Confirmation />);
    const input = string
    html2canvas(input)
      .then((canvas) => {
        const imgData = canvas.toDataURL('image/png');
        const pdf = new jsPDF();
        pdf.addImage(imgData, 'JPEG', 0, 0);
        // pdf.output('dataurlnewwindow');
        pdf.save("download.pdf");
      })
    ;
  }

  render(){
    return(
      <React.Fragment>
        <div className="report">
          <div className="title">
          <Segment placeholder>
    <Header icon>
      <Icon name='pdf file outline' />
      Here is your report.
    </Header>
    <Button primary onClick={this.printDocument}>Print Report</Button>
  </Segment>
            {/* <h1>Thank you</h1> */}
            {/* <button className="reportBtn pdfBtn" type="submit">Download Pdf</button> */}
          </div>
        </div>
        <Link className="returnBtn" to="/"><a className="textBtn">Return</a></Link>
      </React.Fragment>
      )
  }
}
