import { FormGroup, Label, Input } from 'reactstrap';
import React, { PureComponent } from 'react'

class Comments extends PureComponent {

  state = {
    comment: "",
  }

  async handleChange(event) {
    var val = event.target.value
    await this.setState({
      comment: event.target.value
    })
    console.log("value =====>", val)
    //console.log(typeof(event.target.value))
    //console.log(event.target.value)
    console.log(this.state.comment)
  }
  async onblur() {
    const questionId = this.props.listNameFromParent
    await localStorage.setItem("comments", this.state.comment)
    var commentStorage = localStorage.getItem("comments")
    questionId.comment = commentStorage
    console.log("CommentStorage ===>", commentStorage)
  }

  render() {

    return <FormGroup>
      <Label for="exampleText">Comment</Label>
      <Input
        style={{ margin: '0', width: "100%", }}
        type="textarea"
        id="exampleText"
        onBlur={() => this.onblur()}
        value={this.state.comment}
        name="comment"
        placeholder="Place comment if needed"
        onChange={this.handleChange.bind(this)}
      // onClick={(a) => {
      //   // console.log(a.target.value)
      //   localStorage.setItem("comments", a.target.value)
      //   var commentsStorage = localStorage.getItem("comments")
      //   questionId.comment = commentsStorage
      //   //console.log(questionId)
      // }
      // }
      />
    </FormGroup>

  };
}

export default Comments;
