import Mode from '../ModeSelection/Mode'
import MainForm from './Mainform/MainForm'
import Legend from './Mainform/Legend/Legend'
import React from 'react';
import { Container } from 'reactstrap';



export default class FormPage extends React.Component {
    state = {
        selectedCat: 1,
        selectedMode: 0,
        group: null,
        dataFromStorage: []
    };

    handleSelection = (idx) => (e) => {
        this.setState({ selectedMode: idx });
    };

    selectCat = (e) => {
        this.setState({ selectedCat: e.target.value })
    }

    componentWillMount(){
        var getGroup = localStorage.getItem("group")
        var stateMode = localStorage.getItem("stateMode")
        this.setState({
            group: getGroup, dataFromStorage: stateMode
        })
    }
    componentDidMount(){
        localStorage.setItem("stateMode", JSON.stringify(this.props.location.state))   

        // console.log("state mode ", stateMode)
    }


    // componentDidUpdate(){
    //     let getGroup = localStorage.getItem("group")
    //     let group = this.getUrlParams();
    //     if(this.didQueryChange(group)) this.setState({group: group})
    // }

    render() {
        //const selectedtransport = this.props.location.state.modes.modes.filter(item => item.selected === true);
        // var getMode = Object.values(this.state.dataFromStorage)
        console.log("state mode ", this.state.dataFromStorage)
        // console.log("get mode ", getMode.modes)
        return (
            <React.Fragment>
                <Container>
                    <Legend /> 

                    <div style={{ display: "block", margin: "auto" }}>
                        <h4>{this.state.dataFromStorage.group === 0 ? "Separate" : "Grouped"} Reporting for</h4>

                        <div style={{ display: "flex", justifyContent: "start", marginBottom: "40px" }}>
                            {this.state.dataFromStorage.group === 0 ?

                            this.state.dataFromStorage.modes.modes.filter(mode => mode.selected === true).map((mode, idx) =>
                            <Mode key={idx} id={mode.id} index={idx} name={mode.name} url={mode} selected={this.state.selectedMode === idx ? true : false} maxHeight="50px" handleSelection={this.handleSelection(idx)} grouped={false} />)
                            :                             
                            this.state.dataFromStorage.modes.modes.filter(mode => mode.selected === true).map((mode, idx) =>
                             <Mode key={idx} id={mode.id} index={idx} name={mode.name} url={mode} selected={true} maxHeight="50px" grouped={true}  />) }  
                        </div>


                    </div>


                    {this.state.dataFromStorage.modes.modes.filter(mode => mode.selected === true).map((mode, idx) => idx === this.state.selectedMode &&
                        <MainForm key={idx} id={mode.id} group={this.state.dataFromStorage.group} index={idx} />
                    )}


                </Container>
            </React.Fragment>
        )
    }
}
