import { ButtonGroup, Button } from 'reactstrap';
import axios from 'axios';
import URLStorage from '../../../../constants';
import uniqueid from 'uniqid';
import './Radio.css';

import React, { PureComponent } from 'react'

class Frequency extends PureComponent {

    state = {
        regularity: [],
        regularityId: 0,
        active: null,
        id: 0,
    }

    // compare() {

    //     if (!this.props.prevFreq) return <Alert>No result previous year</Alert>
    //     if (this.props.value > this.props.prevFreq) return <Alert><img src="./RadioIcons/up.svg"  alt="UP"/></Alert>
    //     else if (this.props.value < this.props.prevFreq) return <Alert><img src="./RadioIcons/down.svg"  alt="DOWN"/></Alert>
    //     else if (this.props.value === this.props.prevFreq) return <Alert><img src="./RadioIcons/stable.svg" alt="Stable"/></Alert>
    //     else if (this.props.value === null) return <Alert>_</Alert>
    // }

    // onclick(e){
    //     className={this.state.regularity[index] === this.props.idFrequency ? "active" : "inactive"}

    // }

    componentDidMount() {
        axios.get(`${URLStorage.API_URL}/getallfrequency`)
            .then(res => {
                const data = res.data
                this.setState({
                    regularity: data,
                    isLoaded: true,
                })
            })
        // localStorage.getItem("regularityId")

    }

    funFunc = () => {
        const { regularityCall } = this.state.regularityId
        this.props.callBackFromParent(regularityCall)
    }


    // componentDidUpdate(){
    //     localStorage.setItem("regularityId",   
    //     JSON.stringify(this.state.regularityId))
    // }




    render() {

        const questionId = this.props.listNameFromParent

        // console.log(questionId)

        return (


            <>
                <ButtonGroup className='squareRadio' name="frequency">
                    {this.state.regularity.map((frequency, index) => {
                        return (
                            <Button
                                className={this.state.active === index ? `active` : null}
                                // onClick = {this.props.onClick}
                                onClick={(a) => {
                                        this.setState({ active: index, regularityId: index })
                                        this.funFunc()
                                        localStorage.setItem("frequency" + JSON.parse(questionId), a.target.value);

                                        var frequencyStorage = parseInt(localStorage.getItem("frequency"))
                                        questionId.frequency = frequencyStorage
                                        // console.log(frequencyStorage)
                                        console.log(questionId)
                                    }
                                }
                                name="frequency"
                                key={index}
                                value={index}

                            >
                                {frequency.regularity}
                            </Button>
                        );
                    })}
                </ButtonGroup>
            </>
        )
    }
}

export default Frequency;
