export default class DropdownMotivation extends React.Component {
  state = {
      motivations: [],
  }




  componentDidMount(){

    axios.get(`${URLStorage.API_URL}/getallmotivations`)
        .then(res => {
            this.setState({ motivations: res.data });
            localStorage.setItem("motivation", JSON.stringify(res.data));
        })
  }

  handleChange = (e, { value }) => this.setState({ value })

  render() {



    const getOptions = (number, prefix = 'Choice ') =>
  _.times(number, index => ({
    key: index,
    text: `${prefix}${index}`,
    value: index,
  }))


 return (
      <Grid columns={2}>
        <Grid.Column>
          <Dropdown
            onChange={this.handleChange}
            options= { this.state.motivations.map(item => <ul key={uniqueid()}><li key={uniqueid()}>{item.motivation}</li></ul>)}
            placeholder='Choose an option'
            selection
          />

        </Grid.Column>

      </Grid>
    )
  }
}

