import Frequency from './Frequency/Radio';
import Comments from './Comment/Comment';
// import Legend from './Legend/Legend';
import Motivation from './Motivation/Motivation'
import Categories from './Categories/Categories'
import URLStorage from '../../../constants';
import { Link } from 'react-router-dom';
import uniqueid from 'uniqid';
// import  ConfirmModal  from '../Modal';

import '../../../App.css';

import axios from 'axios';
import React from 'react';
import { Container, Row, Col, Button, Form, FormGroup } from 'reactstrap';

import './MainForm.css'
import DropdownExampleCompact from './Motivation/Motivation';

export default class MainForm extends React.Component {

    state = {

        // --------- TESTING : UNCOMMENT WHEN SERVER DOWN ----------------
        categories: [],
        motivations: [],
        regularity:[],
        questions: [],
        questionId: [],
        id: 2,
        // ----------------------------------------------------------------

        answers: [],
        value: [],
        selectedCat: 1,
        selectedMode: 0,

    };

    handleSelection = (idx) => (e) => {
        this.setState({ selectedMode: idx });
    };

    next = () => {
        this.setState(prevState => ({ selectedCat: prevState.selectedCat + 1 }));
    }

    prev = () => {
        this.setState(prevState => ({ selectedCat: prevState.selectedCat - 1 }));
    }

    anyChange = (id) => (e) => {
        const inputName = e.target.name;
        const inputValue = e.target.value;
        let entries = Object.assign({}, this.state);
        const { answers } = entries;
        answers.map(answer => answer.questionID === id ? answer[inputName] = inputValue : undefined);
        this.setState(entries);
        localStorage.setItem(this.props.id, JSON.stringify(this.state.answers));

        // ------------ SWITCH BOOLEAN TO COMPLETED FOR CURRENT MODE ---------------

        if (this.state.answers.filter(answer => answer.frequency === null).length === 0) {

            let completeMode = JSON.parse(localStorage.getItem("completedModes"));
            completeMode[this.props.index] = true;

            if (this.props.group) {          
                var dummieArray = [];          
                    completeMode.map(item => dummieArray.push(true));  
                    localStorage.setItem("completedModes", JSON.stringify(dummieArray));                      

            } else {
                localStorage.setItem("completedModes", JSON.stringify(completeMode));
            }            
        } 
    }

    selectCat = (e) => {
        this.setState({ selectedCat: e.target.value })
        localStorage.setItem(this.props.id, JSON.stringify(this.state.answers));
    }

    componentDidMount() {

        const transportId = this.props.id;
        this.setState({ modeID: transportId })


        // ------------ TESTING : UNCOMMENT WHEN SERVER DOWN -------
        // const localanswers = localStorage.getItem(this.props.id);
        // console.log(localanswers);
        // this.setState({ answers: JSON.parse(localanswers)});
        // console.log("localstorageanswers", this.state.answers);
        //-----------------------------------------------------------


        // ------------ TESTING : COMMENT WHEN SERVER DOWN -------


        // axios.get(`${URLStorage.API_URL}/getallresult`)
        //     .then(res => {
        //         if (res) {

        //             const answers = res.data.map(function (item) {
        //                 return {
        //                     // question: item.question,
        //                     // categoryId: item.id[0],
        //                     // questionID: item.id[1],
        //                     // motivationID: 6,
        //                     // frequency: null,
        //                     // modeID: transportId,
        //                     idUser: item.idUser,
        //                     frequency: item.idFrequency,
        //                     motivation: item.idMotivation,
        //                     question: item.idQuestion,
        //                     comment: "",
        //                     // periode: JSON.parse(localStorage.getItem("periods")).currentPeriod,
        //                     // previousPeriod: JSON.parse(localStorage.getItem("periods")).previousPeriod,
        //                 }
        //             });
        //             this.setState({ answers });
        //             localStorage.setItem(this.props.id, JSON.stringify(answers));

        //             if (!localStorage.getItem(this.props.id)) {
        //                 this.setState({ answers });
        //                 localStorage.setItem(this.props.id, JSON.stringify(this.state.answers));

        //             } else {
        //                 let ans1;
        //                 ans1 = localStorage.getItem(this.props.id);
        //                 this.setState({ answers: JSON.parse(ans1) });
        //             }
        //         }
        //     })
        //     .then(() => {
        //         const localquestions = JSON.parse(localStorage.getItem("questions"));
        //         this.setState({ questions: localquestions });
        //         let ans2;

        //         ans2 = localStorage.getItem(this.props.id);
        //         this.setState({ answers: JSON.parse(ans2) });
        //     })

        // axios.get(`${URLStorage.API_URL}/getallmotivations`)
        //     .then(res => {
        //         this.setState({ motivations: res.data });
        //         localStorage.setItem("motivation", JSON.stringify(res.data));
        //     })






        // ------------------ Retreiving PREVIOUS PERIOD RESULTS ----------------------

        if (JSON.parse(localStorage.getItem("periods"))) {

            axios.post(`${URLStorage.API_URL}/resultPeriode`, {
                ptoID: parseInt(localStorage.getItem("ptoid")),
                periodeID: JSON.parse(localStorage.getItem("periods")).previousPeriod,
            })          
            .then(res => {
                const previousResult = res.data.filter(item => item.mode_id === transportId).sort((a, b) => a.questionID - b.questionID).map(item => {

                    return { prevFreq: item.frequency, };
                })
                const newStateAnswers = this.state.answers.map((answer, idx) => {
                    return { ...answer, ...previousResult[idx] }
                })
        // // ------------------ ADDING PREVIOUS PERIOD RESULTS TO THE STATE.ANSWERS ----------------------
                this.setState({ answers: newStateAnswers });
                localStorage.setItem("prevResult", JSON.stringify(previousResult))
            })
        };
        // // -----------------------------------------------------------------


        axios.get(`${URLStorage.API_URL}/getallincident`)
            .then(res => {
                const data = res.data
                this.setState({
                    categories: data,
                    isLoaded: true,
                })
            })

        axios.get(`${URLStorage.API_URL}/getallfrequency`)
            .then(res => {
                const data = res.data
                this.setState({
                    regularity: data,
                    isLoaded: true,
                })
            })


        axios.get(`${URLStorage.API_URL}/getAllQuestions/${this.state.id}`)
        .then(res => {
            const data = res.data
            this.setState({
                questions: data,
                isLoaded: true,
            })
        })

        axios.get(`${URLStorage.API_URL}/getallmotivations`)
        .then(res => {
            this.setState({ motivations: res.data });
            localStorage.setItem("motivation", JSON.stringify(res.data));
        })


        // axios.get(`${URLStorage.API_URL}/getallquestions`)
        // .then(res => {
        //     const data = res.data
        //     this.setState({
        //         questions: data,
        //         id:[],
        //         isLoaded: true,
        //     })
        // })



    };


    render() {
        // NEXT / SUBMIT BUTTON SWITCH
        const nextSubmit = this.state.selectedCat === 5 ?
            // || (parseInt((localStorage.getItem("group")) === 1)
            (JSON.parse(localStorage.getItem("completedModes")).filter(item => item === false).length === 0)  ?
                <Link to={{ pathname: "/confirmation", state: { answers: this.state.answers } }}>
                    <Button onClick={this.submit} className="submit">Submit</Button>
                </Link>
                : <>
                    <Button className="submit" disabled>Submit</Button>
                </>
            :
            <Button onClick={this.next} className="submit">Next</Button>;


        // async function getAnswers(){
        //     const { answers } = await this.state;
        // }

        // getAnswers();
        const { answers } = this.state;



        console.log(this.state.categories);
        // console.log(this.state.regularity);
        // console.log(this.state.motivations);
        //console.log(this.state.questions);
        // console.log(this.state.answers);
        // console.log(this.state);

        const { categories, active } = this.props;

        return (
            <React.Fragment>
                <Container>

                    <div style={{ display: "flex", justifyContent: "flex-end" }}>
                        {this.state.selectedCat !== 1 && <Button style={{ marginRight: "10px" }} onClick={this.prev} className="submit" >Previous</Button>}
                        {nextSubmit}
                    </div>
                    {/* <Categories categories={this.state.categories} parentCallback={this.selectCat} active={this.state.selectedCat} /> */}


                    {/* <ul>
                    { this.state.categories.map(categories => <li key={uniqueid()}>{categories.name}</li>)}
                    </ul> */}


                    <ul style={{display: "flex", justifyContent: "space-around"}}>
                    { this.state.categories.map(categories => <li style={{fontSize: "15px", padding: "5px", textAlign:"center"}} key={uniqueid()} onClick={this.props.parentCallback}>{categories.name}</li>)}
                    </ul>


                    {/* {this.state.categories.map((categorie, i) => {
                        return (
                                <ul>
                                <li key={i} >
                                {categorie.name}
                                </li>
                            </ul>
                        );
                        })} */}
                    <br />
                    <ul>
                    { this.state.motivations.map(item => <li key={uniqueid()}>{item.motivation}</li>)}
                    </ul>                    
                     <br />
                     <ul>
                     {this.state.regularity.map((item) => <li key={uniqueid()}>{item.regularity}</li>)}
                     </ul>
                    {/* <Motivation /> */}
                    {/* < DropdownExampleCompact /> */}
                    {/* <Frequency /> */}

                </Container>
            </React.Fragment>
        )
    }
}
