        //     axios.post(`${URLStorage.API_URL}/resultPeriode`, {
        //         ptoID: parseInt(localStorage.getItem("ptoid")),
        //         periodeID: JSON.parse(localStorage.getItem("periods")).previousPeriod,
        //     })          
        //     .then(res => {
        //         const previousResult = res.data.filter(item => item.mode_id === transportId).sort((a, b) => a.questionID - b.questionID).map(item => {

        //             return { prevFreq: item.frequency, };
        //         })
        //         const newStateAnswers = this.state.answers.map((answer, idx) => {
        //             return { ...answer, ...previousResult[idx] }
        //         })
        // // ------------------ ADDING PREVIOUS PERIOD RESULTS TO THE STATE.ANSWERS ----------------------
        //         this.setState({ answers: newStateAnswers });
        //         localStorage.setItem("prevResult", JSON.stringify(previousResult))
        //     })
        // };
        // // -----------------------------------------------------------------

    };


    render() {
        // NEXT / SUBMIT BUTTON SWITCH
        const nextSubmit = this.state.selectedCat === 5 ?
            // || (parseInt((localStorage.getItem("group")) === 1)
            (JSON.parse(localStorage.getItem("completedModes")).filter(item => item === false).length === 0)  ?
                <Link to={{ pathname: "/confirmation", state: { answers: this.state.answers } }}>
                    <Button onClick={this.submit} className="submit">Submit</Button>
                </Link>
                : <>
                    <Button className="submit" disabled>Submit</Button>
                </>
            :
            <Button onClick={this.next} className="submit">Next</Button>;



        const { answers } = this.state;

        return (
            <React.Fragment>
                <Container>

                    <div style={{ display: "flex", justifyContent: "flex-end" }}>
                        {this.state.selectedCat !== 1 && <Button style={{ marginRight: "10px" }} onClick={this.prev} className="submit" >Previous</Button>}
                        {nextSubmit}
                    </div>
                    <Categories categories={this.state.categories} parentCallback={this.selectCat} active={this.state.selectedCat} />

                    <Form>
                        {answers.length && answers
                            .filter((item, idx) => item.categoryId === this.state.selectedCat)
                            .map((data, index) => {

                                return <Container key={index} className="dark-overlay" >
                                    <Row key={index} style={{ padding: '15px' }}>
                                        <Col key={index} sm="12" md={{ size: 12 }}>

                                            <FormGroup key={index}>
                                                <h6> {index + 1} : {data.question} </h6>

                                                <Frequency key={data.questionID} questionId={data.questionID} value={data.frequency} parentCallback={this.anyChange(data.questionID)} prevFreq={data.prevFreq} />

                                            </FormGroup>

                                            <Motivation key={data.questionID} questionId={data.questionID} items={this.state.motivation} value={data.motivationID} parentCallback={this.anyChange(data.questionID)} />

                                            <Comments key={data.questionID + 'coucou'} questionId={data.questionID} comment={data.comment} parentCallback={this.anyChange(data.questionID)} />
                                        </Col>


                                    </Row>
                                </Container>
                            })}
                        <div style={{ display: "block" }}>
                            <Categories categories={this.state.categories} parentCallback={this.selectCat} active={this.state.selectedCat} />
                            <div style={{ display: "flex", justifyContent: "flex-end" }}>
                                {this.state.selectedCat !== 1 && <Button style={{ marginRight: "10px" }} onClick={this.prev} className="submit" >Previous</Button>}
                                {nextSubmit}
                            </div>
                        </div>





                    </Form>



                </Container>
            </React.Fragment>
        )
    }
}
