import React from 'react';
import { FormGroup, Label, Input } from 'reactstrap';
import MainForm from '../MainForm'

const Motivation = props => {


    return       <FormGroup>
    <Label >Motivation</Label>
    <Input value={props.value} onChange={props.parentCallback}>
        {/* {props.items.map((motivations, idx) =>
            <option key={idx} value={motivations.id}>{motivations.motivations}</option>)} */}
            { this.state.motivations.map(motivations => <option>{motivations.motivation}</option>)}
    </Input>    
</FormGroup>

}

export default Motivation;
