import _ from 'lodash'
import { Dropdown } from 'semantic-ui-react'

const getOptions = (motivations, prefix = 'Choice ') =>

axios.get(`${URLStorage.API_URL}/getallmotivations`)
.then(res => {
    this.setState({ motivations: res.data });
    localStorage.setItem("motivation", JSON.stringify(res.data));
})

  _.times(motivations, index => ({
    key: index,
    text: `${motivations}${index}`,
    value: index,
  }))

const DropdownExampleCompact = () => (
  <Dropdown
    placeholder='Compact'
    compact
    selection
    options={getOptions(3, '')}
  />
)

export default DropdownExampleCompact
