                        console.log('has found matching IDs')
                        var indexOf = this.state.allData.indexOf(data);
                        console.log("index of ", indexOf)
                        for (var key in data) {
                            if (data[key] === question[key] || data[key] === undefined) {
                                data[key] = question[key]
                                this.state.allData.splice(indexOf, 1)
                            }
                        }
                        return true
                    } else {
                        console.log("has not found matching IDs")
                        return false
                    }
                })
